import React from "react";
import './Movies.css';
import DatePicker from "react-datepicker";
import { Link } from "react-router-dom";

class Bookingpage extends React.Component{
    constructor(){
        super()
        this.state=({
            date:''
        })
    }
    selectDate = (e) => {
        this.setState({date:e})
  }
render(){
  return (
    <div>
            <div className="container ">
                <ul className="list-group">
                <DatePicker selected={this.state.date} dateFormat="dd-MM-yyyy" onChange={this.selectDate}></DatePicker>
                <li className="list-group-item btn-primary">Available Show Timings</li>
                <li className="list-group-item btn-primary"><Link to='/Seatbooking' >Choose Number of Seats</Link></li>
                <li ><Link to='/Bookingpage'>Book Now</Link></li>
                </ul>
                </div>
        </div>
    );
  } 
}

export default Bookingpage;
