const Bookingpage = () => {
    const [currentDate, setCurrentDate] = useState(new Date());
  
    const handleDateChange = (date) => {
      setCurrentDate(date);
    };
  
    return (
      <div>
        <h1>Current Date and Time</h1>
        <Calendar
          value={currentDate}
          onChange={handleDateChange}
        />
      </div>
    );
  };
  
export default Bookingpage;
