import React,{Component} from "react";
import { Link } from "react-router-dom";

const Recommendedlist= (recommendedmovieslist) => {

    const renderlist= ({recommendedmovieslist}) => {
          if(recommendedmovieslist){
            return recommendedmovieslist.map((data)=>{
                return(
                    <Link key={data._id} to={'/Movies/${data.id}'}>
                          <div className='card col-lg-4 col-md-4 col-12'>
                            <div className='row'>
                             <div className='card-img'>
                                 <img className="card-img-top" src={data.imageUrl} alt={data.name} />
                          </div>
                          <hr />
                          <span className="topTemp">{data.name}</span>
                          </div>
                    </div>
                    </Link>
                )

            });

          }
        
    }
    return(
        <div className="container">
            <div className="row">
                {renderlist(recommendedmovieslist)}
            </div>
        </div>
    )
};
 
export default Recommendedlist;
