import React, {Component} from "react";
import JSON from './recommendedmovies.json';
import Recommendedlist from "./Recommendedlist";

const url="http://localhost:6800/Movies";

class Recommendedmovies extends Component{
    constructor(){
        super();
        this.state={
            Movies: JSON
        }
    }

    componentDidMount(){
        fetch(url,{method:'GET'})
        .then((response) => response.json())
            .then((data)=>{
              this.setState({
                Movies:data
                })
             })

    }
    render(){
        return(
            <div>
                <Recommendedlist recommendedmovieslist={this.state.Movies} />
            </div>
        )
    }
}
export default Recommendedmovies;
