import React from "react";
import './Movies.css';
import { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import { Link } from "react-router-dom";

function Bookingpage() {
  const [date, setDate] = useState(new Date());

  return (
          <><div className="container ">
          <DatePicker
              showTimeSelect
              minTime={new Date(0, 0, 0, 12, 30)}
              maxTime={new Date(0, 0, 0, 19, 0)}
              selected={date}
              onChange={(date) => setDate(date)} />
      </div>

          <div class="card">
           <div class="card-header">
                Book Tickets
           </div>
            <ul class="list-group">
                <li className="list-group-item btn-primary">Date</li>
                <li className="list-group-item btn-primary">Available Show Timings</li>
                <li className='btn-primary'><Link to='/Seatbooking' className='nav-link'>Choose Number of Seats</Link></li>
                <li ><Link to='/Bookingpage'>Book Now</Link></li>
            </ul>
            </div>
          </>
     
  );
}

export default Bookingpage;
