import React from "react";
import {BrowserRouter, Route, Routes, Link} from 'react-router-dom';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

class Home extends React.Component{
    constructor(){
        super();
        this.state={
            title: 'Home Page',
        }
    }

    render(){
        return(
    <div>
            <div className='container-fluid'>
                <ul>
                    <button type='button' className='col-lg-4 col-md-4 col-12 details'>
                        <li><Link to='/latest' className='nav-link'>Latest Movies</Link></li>
                    </button>
                    <div className='col-lg-4 col-md-4 col-12 details'>
                        <a href='#' className='nav-link'>Upcoming Movies</a>
                    </div>
                    <div className='col-lg-4 col-md-4 col-12 details'>
                        <a href='#' className='nav-link'>NearBy Events</a>
                    </div>
                </ul>
            </div>
            <Carousel autoPlay interval="5000" transitionTime="5000" showThumbs={false}>
                    <div className='item'>
                        <img src='https://wallpapercave.com/wp/wp6674867.jpg' />
                    </div>
                    <div className='item'>
                        <img src='https://c4.wallpaperflare.com/wallpaper/862/449/162/jack-reacher-star-wars-interstellar-movie-john-wick-wallpaper-preview.jpg' />
                    </div>
                    <div className='item'>
                        <img src='https://wallpapers.com/images/featured/movie-9pvmdtvz4cb0xl37.jpg' />
                    </div>
                </Carousel>
                </div>
        )
    }
}
export default Home;
