import React from "react";
import './Movies.css';
import { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import { Link } from "react-router-dom";

function Bookingpage() {
  const [date, setDate] = useState(new Date());

  return (
          <><div className="container ">
          <DatePicker
              showTimeSelect
              minTime={new Date(0, 0, 0, 12, 30)}
              maxTime={new Date(0, 0, 0, 19, 0)}
              selected={date}
              onChange={(date) => setDate(date)} />
      </div><div className="container">
              <ul>
                  <button type='button' className='col-lg-4 col-md-4 col-12 btn btn-primary'>
                      <li><Link to='/Seatbooking' className='nav-link'>Choose Number of Seats</Link></li>
                  </button>
              </ul>
          </div>
          <div class="card">
           <div class="card-header">
                Featured
           </div>
            <ul class="list-group">
                <li class="list-group-item">An item</li>
                <li class="list-group-item">A second item</li>
                <li class="list-group-item">A third item</li>
                <li><Link to='/Bookingpage'>Book Now</Link></li>
            </ul>
            </div>
          </>
     
  );
}

export default Bookingpage;
